'''QR Code Encode / Generator'''import pyqrcodeqr = pyqrcode.create('from pyzbar.pyzbar import decode, ZBarSymbol')#The data that you want to store')qr.png('qr.png', scale=10)'''QR Code Decoder / Scanned Output'''import openpyxl	from pyzbar.pyzbar import decode, ZBarSymbol	from PIL import Imagedecoded = decode(Image.open("qr.png"),symbols=[ZBarSymbol.QRCODE])wb = openpyxl.load_workbook(filename = "S:\\Git\\Dump\\! EDIT ME.xlsx", data_only=True) cell = wb['EnDeQR']['C2']cell.value = str(decoded)detext = wb['EnDeQR']['C4']print(detext.value)wb.save("S:\\Git\\Dump\\! EDIT ME.xlsx")