'''QR Code Encode / Generator'''import qrcode# Create qr code instanceqr = qrcode.QRCode(    version = 1,    error_correction = qrcode.constants.ERROR_CORRECT_H,    box_size = 10,    border = 4,)# The data that you want to storedata = "The data that you want to store"# Add dataqr.add_data(data)qr.make(fit=True)# Create an image from the QR Code instanceimg = qr.make_image()# Save it somewhere, change the extension as needed:# img.save("image.png")# img.save("image.bmp")# img.save("image.jpeg")img.save("img.png")import pyqrcodeqr = pyqrcode.create('The data that you want to store')qr.png('qr.png', scale=10)'''QR Code Decoder / Scanned Output'''	from pyzbar.pyzbar import decode, ZBarSymbol	from PIL import Imagedecoded = decode(Image.open("qc.jpg"),symbols=[ZBarSymbol.CODE128])print(decoded)